/**
 * @name xue.alert.min.js
 * @description 提示信息
 * 
 * @module 
 * @submodule 
 * @main 
 * @class 
 * @constructor 
 * @static 
 */

/**
 * 弹出提示
 * @param  {string}   msg   弹窗内要显示的内容
 * @param  {Function} fn    点击确定按钮的回调函数
 * @return {[type]}         [description]
 */
ui.alert = ui.alert || function(msg, fn){
	var arg = arguments, 
		tp = null, msg = false, fn = false;
	
	$.each(arg, function(k, v){
		tp = typeof v;
		msg    = tp === 'string'   ? v : msg ;
		fn     = tp === 'function' ? v : fn ;
	});
	
	return xue.dialog({
		id      : 'alert',
		content : msg,
		ok      : fn
	});

};



xue.extend('alert', ui.alert);
